function reloadOptionsOnSave(){
    if(GM_config.get('tbtThumbnailEnable')){
        resizeThumbnails();
    } else {
        resizeThumbnails(true);
    };
    if(GM_config.get('tbtGenreSelection')){
        genreSelection();
    } else {
        genreSelection(true);
    };
}

$(document).ready(function(){
    if(GM_config.get('tbtThumbnailEnable')){
        resizeThumbnails()
    }
    if(GM_config.get('tbtGenreSelection')){
        genreSelection()
    }
})
